{let t=function(){let t=$("#new-post-form");t.submit((function(s){s.preventDefault(),$.ajax({type:"post",url:"/posts/create",data:t.serialize(),success:function(t){let s=e(t.data.post);$("#posts-list-container>ul").prepend(s),n($(" .delete-post-button",s)),new PostComments(t.data.post._id),new ToggleLike($(".toggle-like-button",s)),new notify({theme:"relax",text:"Post Published",type:"success",layout:"topRight",timeOut:1500}).show()},error:function(t){console.log(t.responseText)}})}))},e=function(t){return $(`<li id="post-${t._id}">\n      <p>\n        ${t.content} \n    \n        <small> ${t.user.name}  </small>\n        \n    \n        <small>\n          <a class="delete-post-button"href="/posts/destroy/<%= post.id%>"\n            ><i class="fa-solid fa-delete-left"></i\n          ></a>\n        </small>\n        <br>\n        <small>\n                            \n         <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${t._id}&type=Post">\n             0  <i style="color: red" class="fa-solid fa-heart"></i>\n         </a>\n                            \n        </small>\n    \n       \n      </p>\n    \n      <div class="PostComments">\n       \n        <form action="/comments/create" method="POST">\n          <input\n            type="text"\n            name="content"\n            id="Type here to add comment....."\n            required\n          />\n        <input type="hidden" name="post" value="${t._id}" />\n          <input type="submit" value="Add Comment" />\n        </form>\n        \n    \n        <div class="post-comments-list">\n          <ul id="post-comments -${t._id}">\n            \n          </ul>\n        </div>\n      </div>\n    </li>\n    `)},n=function(t){$(t).click((function(e){e.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),success:function(t){$(`#post-${t.data.post._id}`).remove()},error:function(t){console.log(t.responseText)}})}))};t()}